import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';

import Heading from './heading'

const fname = "Emeldine";
const lname = "Tingiweh";
const num = 7;

const customStyle = {
  color: "",
}

const dateTime = new Date();

const currentDateTime = dateTime.getHours();

let greetings;

if(currentDateTime < 12){
  greetings = "Good Morning"
  customStyle.color = "Blue"
} else if(currentDateTime < 18){
  greetings = "Good Afternoon";
  customStyle.color = "red"
}else{
  greetings = "Good Evening"
  customStyle.color = "green"
}

customStyle.color = "blue";
ReactDOM.render(
  <div>
  <h1 className ="heading" style= {customStyle}> {greetings} {`${lname} ${fname}`} </h1>
  <Heading />
   <ul>
     <li>Irish potatoes</li>
     <li>Fufu corn</li>
     <li>Dial</li>
   </ul>

   <p> My lucky number is {num} </p>
   <p> And yours lucky number is {Math.floor(Math.random()*10)} </p>
  </div>,
  document.getElementById('root')
);

// var h1 = document.createElement("h1");
// h1.innerHTML = "Hello World";
// document.getElementById("root").appendChild(h1);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
