import React, { useState } from "react";

function App() {

  let now = new Date().toLocaleDateString()

  const [time,setTime] = useState(now)
  function getTime(){
    setState(now);
  }
  return (
    <div className="container">
      <h1>{time}</h1>
      <button onClick={getTime}>Get Time</button>
    </div>
  );
}

export default App;
